-- ----------------------------
-- Table structure for d_around
-- ----------------------------
DROP TABLE IF EXISTS `d_around`;
CREATE TABLE `d_around` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `class_id` int(11) DEFAULT NULL COMMENT '所属类型id',
  `thumbnail` text,
  `name` varchar(50) DEFAULT NULL,
  `price` decimal(10,0) DEFAULT NULL,
  `brief` varchar(100) DEFAULT NULL,
  `detail` text,
  `state` tinyint(2) DEFAULT '1',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of d_around
-- ----------------------------
INSERT INTO `d_around` VALUES ('1', '2', '/ontheroad/Uploads/aroound/593ec6dbb34e8.jpg', '手表', '56', '手表，或称为腕表，是指戴在手腕上，用以计时/显示时间的仪器', '&lt;p style=&quot;font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;&quot;&gt;&lt;img src=&quot;/ontheroad/Public/Admin/kindeditor/attached/image/20170612/20170612165438_45713.jpg&quot; alt=&quot;&quot; /&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;&quot;&gt;手表工作原理，手表是用来指示时间的一种精密仪器，该仪器的原理是利用一个周期恒定的、持续振动的振动系统作为标准。如果知道了振动系统完成一手表次全振动所需要的时间（振动周期），并计算出振动次数，那么，振动这么多次之后所经历的时间就等于振动周期乘以振动次数。即时间=振动周期×振动次数&lt;/span&gt;。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:14px;color:#333333;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;span style=&quot;color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;&quot;&gt;从19世纪中期有人将计时挂表装上皮带，戴在手腕上使用开始，逐步改进、缩小体形、美化样式，发展成为手表。&lt;/span&gt;\r\n&lt;/p&gt;', '1');
INSERT INTO `d_around` VALUES ('2', '2', '/ontheroad/Uploads/aroound/593ecd029ba26.png', '火影', '99', '每一位年轻的忍者都在开拓着属于自己的忍道', '&lt;p&gt;\r\n	&lt;span style=&quot;color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;&quot;&gt;&lt;img src=&quot;/ontheroad/Public/Admin/kindeditor/attached/image/20170612/20170612172016_63517.png&quot; alt=&quot;&quot; /&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#333333;font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;&quot;&gt;作品设定在一个忍者的世界，故事从主人公漩涡鸣人的孤儿生活开始，他的父母为了保护村子，将攻击村子九尾妖狐封印到了他体内，鸣人因此受尽了村人的冷落，只是拼命用各种恶作剧试图吸引大家的注意力。好在还是有依鲁卡老师的关心，鸣人的性格才没有变得扭曲，他总是干劲十足、非常乐观。&lt;/span&gt;\r\n&lt;/p&gt;', '1');
INSERT INTO `d_around` VALUES ('3', '1', '/ontheroad/Uploads/aroound/59452543f40b6.png', '黑色旅行T桖', '99', '专为旅行打造的一款T桖', '&lt;span style=&quot;color:#111111;font-family:Arial, Helvetica, sans-serif;font-size:13px;background-color:#FFFFFF;&quot;&gt;追梦，又一年。在新的一个创业年里，我有一个梦想，每天送一个床位，给一个陌生人。也许你认识我，也许你住过追梦，也许你来过成都，也许你还没有住过青旅渴望尝试一次，又或者你是长期瓢泼在外的旅者，你是死宅的黄花大闺女，你是除了不能干什么&lt;/span&gt;', '1');
INSERT INTO `d_around` VALUES ('4', '6', '/ontheroad/Uploads/aroound/59452571bed21.png', '糖醋里脊', '12', '糖醋里脊 色泽红亮 酸甜味美', '&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;糖醋里脊&amp;nbsp;色泽红亮，酸甜味美，外酥里嫩的口感深受广大女性朋友的喜欢。也是许多外国友人到中餐馆必点的一道菜&amp;nbsp;，韩剧中就常常会出现这样的情节。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;糖醋里脊，在浙江菜，川菜，鲁菜甚至东北菜里都有，可见人们对它的喜爱程度。&lt;/span&gt;&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;此菜属于焦熘类，想要使里脊外焦里嫩的秘诀是：一定要六成油温先炸定型，待油温升高后再进行复炸1~2遍哦！&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;&lt;img src=&quot;/ontheroad/Public/Admin/kindeditor/attached/image/20170617/20170617125126_35636.png&quot; alt=&quot;&quot; /&gt;&lt;br /&gt;\r\n&lt;/span&gt; \r\n&lt;/p&gt;', '1');
INSERT INTO `d_around` VALUES ('5', '6', '/ontheroad/Uploads/aroound/5945263215a35.png', '香煎土豆里脊肉', '13', '这是一道专门做给宝宝吃的菜', '&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;这是一道专门做给宝宝吃的菜。&lt;/span&gt;&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;香煎里脊肉，肉嫩味香，蘸上番茄沙司，加上土豆，宝宝非常喜欢。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;&lt;img src=&quot;/ontheroad/Public/Admin/kindeditor/attached/image/20170617/20170617125409_39858.png&quot; alt=&quot;&quot; /&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n&lt;/p&gt;', '1');
INSERT INTO `d_around` VALUES ('6', '6', '/ontheroad/Uploads/aroound/594526d564dcd.png', '韭菜鹅蛋蒸饺', '15', '窗台上的韭菜又熟了', '&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;窗台上的韭菜又熟了。老公非常喜欢吃韭菜饺子。正好家里还有几个鹅蛋，就包个韭菜鹅蛋饺子吧。&lt;/span&gt;&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;夏天吃蒸饺不错，再煮锅粥就全了。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;&lt;img src=&quot;/ontheroad/Public/Admin/kindeditor/attached/image/20170617/20170617125536_10681.png&quot; alt=&quot;&quot; /&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n&lt;/p&gt;', '1');
INSERT INTO `d_around` VALUES ('7', '6', '/ontheroad/Public/WeChat/images/order/homeHeader.png', '谷物牛奶', '10', '营养丰富，味道极赞', '&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;特别加入了三种谷物烹制的牛奶，营养丰富，味道极赞～无论是作为早餐还是日常饮品，都是不错的选择。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;市场上的麦片种类很多，为了保证谷物入锅后可以迅速融化，这里我们选用的是快熟麦片。千万不要用普通的大颗粒麦片&lt;/span&gt;', '1');
INSERT INTO `d_around` VALUES ('8', '6', '/ontheroad/Public/WeChat/images/order/homeHeader.png', '面包机版芒果酸奶', '15', '每天喝点酸奶已经成了很多人的饮食习惯', '&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;每天喝点酸奶已经成了很多人的饮食习惯。由于酸奶不需加热，喝起来方便，因此有人大量饮用，有人饿了用它来裹腹。但从营养的角度说，喝酸奶也讲究时间，多饮还会给身体带来负面效果喔。一般饭后饮用有利于吸收营养，晚上喝补钙效果最好。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;酸奶是食物中钙的良好来源，从补钙的角度看，晚上喝酸奶好处更多。因为晚间12点至凌晨是人体血钙含量最低的时候，有利于食物中钙的吸收。同时，这一时间段中人体内影响钙吸收的因素也较少。虽然牛奶中也含有很高的钙，但与它比起来，酸奶中所含的乳酸与钙结合，更能起到促进钙吸收的作用。&lt;/span&gt;&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;不过，晚上喝酸奶时一定要记住，酸奶中的某些菌种及酸性物质对牙齿有一定的损害，喝完后应及时刷牙&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;&lt;img src=&quot;/ontheroad/Public/Admin/kindeditor/attached/image/20170617/20170617125749_14844.png&quot; alt=&quot;&quot; /&gt;&lt;br /&gt;\r\n&lt;/span&gt;\r\n&lt;/p&gt;', '1');
INSERT INTO `d_around` VALUES ('9', '6', '/ontheroad/Uploads/aroound/5945279037f7d.png', '番茄手挤面', '16', '番茄简直是面的好搭档', '&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;番茄简直是面的好搭档，煸炒出汁后拌入面条，好吃到飞起。自从会了“手挤面”新技能，百做不腻。把面糊装入裱花袋，剪开一个小口，挤入沸水煮一会，就能做出香浓Q弹的面条啦，很简单吧。&lt;/span&gt;&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;span style=&quot;color:#333333;font-family:Tahoma, Arial, Helvetica, 宋体, &amp;quot;font-size:14px;background-color:#FFFFFF;&quot;&gt;加了接近一半的全麦面粉，大大增加了面粉的营养。1岁以上的宝宝可以慢慢将粗粮加起来。其他蔬菜和虾的营养不多说了，只要记住：吃的食物种类越多越好，这就是全面营养的保障！&lt;/span&gt;', '1');

