-- ----------------------------
-- Table structure for d_document_for_multi
-- ----------------------------
DROP TABLE IF EXISTS `d_document_for_multi`;
CREATE TABLE `d_document_for_multi` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL,
  `thumbnail` varchar(255) DEFAULT NULL,
  `content` text,
  `type` int(11) DEFAULT NULL,
  `time` datetime DEFAULT NULL,
  `hot_degress` int(11) DEFAULT '1',
  `state` tinyint(2) DEFAULT '1',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of d_document_for_multi
-- ----------------------------
INSERT INTO `d_document_for_multi` VALUES ('1', '近距离接触英伟达专业级显卡Quadro系列，都能干点啥？', '/ontheroad/Uploads/gallery//58f32a27598f7.jpg', '&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	英伟达在北京国家会议中心举行媒体发布会。会上英伟达展示了其发布不久的基于Pascal&amp;nbsp;架构的Quadro系列显卡，包括：GP100、P2000、P4000、P5000、P6000&amp;nbsp;等。&lt;span style=&quot;line-height:1.8;&quot;&gt;不同于Geforce的消费级娱乐定位，Quadro立足高端，面向专业级的需求，英伟达的愿景是让它们能够将台式工作站转变为具有突破性能力的超级计算机，并为不同行业的专业任务提供保障。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:0px;line-height:0;&quot;&gt;&lt;/span&gt;&lt;img id=&quot;content-first-img&quot; src=&quot;http://uppic.fd.zol-img.com.cn/t_s500x2000/g5/M00/0B/0F/ChMkJ1jyDXaITYeKAANQojkO0fQAAbr3wPnANMAA1C6115.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	&lt;em&gt;&lt;span style=&quot;font-size:14px;&quot;&gt;Quadro系列显卡（雷锋网现场拍摄）&lt;/span&gt;&lt;/em&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	Pascal&lt;span style=&quot;font-size:13.3333px;line-height:24px;&quot;&gt;&amp;nbsp;&lt;/span&gt;架构的全新NVIDIA&amp;nbsp;Quadro产品阵容致力于为企业提供视觉计算平台，简化设计和仿真工作流程。与上一代产品相比，新Quadro产品不仅具有超快速内存，而且速度最多可以提升两倍（基于SPECviewperf&amp;nbsp;12&amp;nbsp;基准的计算结果）。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	其中GP100为该系列的旗舰产品，被科技媒体和粉丝们称为超当量核弹。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:20px;font-weight:bold;line-height:1.8;&quot;&gt;Quadro&amp;nbsp;GP100参数及系列特点&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;line-height:1.8;font-size:18px;&quot;&gt;&amp;nbsp;GP100&lt;/span&gt;&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	GP100基于&amp;nbsp;Pascal&amp;nbsp;核心，匹配16GB&amp;nbsp;HBM2显存，单精度CUDA核心数&amp;nbsp;3584个，双精度CUDA核心数&amp;nbsp;1792个，半精/单精/双精浮点运算性能分别是20TFLOPS、10TFLOPS和5TFLOPS，带宽高达720GB/s，功耗235W&amp;nbsp;。同其他新发布的专业卡一样，它的性能提升也是全方面的（比如同步多重投影、VRWorks、Iray&amp;nbsp;VR），同时还强化了虚拟化、Sync&amp;nbsp;II、双路NVlink等。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	这也是NVlink首次在Windows系统上获得支持，此前仅仅用于IBM&amp;nbsp;Power8系统和基于Linux的DGX-1，带宽最高80GB/s。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img src=&quot;http://uppic.fd.zol-img.com.cn/t_s500x2000/g5/M00/0B/0F/ChMkJ1jyDXaIa4U1AAH8qL9RPrYAAbr3wPqUY0AAfzA626.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;这个系列官方给出的特点如下：&lt;/span&gt;&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;ul class=&quot; list-paddingleft-2&quot; style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;&quot;&gt;\r\n	&lt;li&gt;\r\n		&lt;p style=&quot;font-family:&amp;quot;&quot;&gt;\r\n			统一仿真、HPC、渲染和设计&amp;nbsp;–&amp;nbsp;GP100将前所未有的双精度性能与16GB的高带宽内存（HBM2）相结合，因此用户可以在设计过程中进行仿真，并以前所未有的速度采集逼真的多物理场仿真。客户&lt;strong&gt;能够将两个GP100&amp;nbsp;GPU与NVLink?技术相结合，并扩展到32GB的HBM2&lt;/strong&gt;，以在单一工作站上创建庞大的可视化计算解决方案。\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img src=&quot;http://uppic.fd.zol-img.com.cn/t_s500x2000/g5/M00/0B/0F/ChMkJ1jyDXeIMd5iAAKRBDTGvbsAAbr3wPst-0AApEc101.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:14px;&quot;&gt;&lt;em&gt;扩展后的Quadro&amp;nbsp;P6000（雷锋网现场拍摄）&lt;/em&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;ul class=&quot; list-paddingleft-2&quot; style=&quot;color:#333333;font-family:&amp;quot;font-size:16px;&quot;&gt;\r\n	&lt;li&gt;\r\n		&lt;p style=&quot;font-family:&amp;quot;&quot;&gt;\r\n			探索深度学习&amp;nbsp;–&amp;nbsp;GP100提供超过20&amp;nbsp;TFLOPS的半精度计算，可谓在Windows和Linux环境中进行深度学习的理想开发平台。\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;p style=&quot;font-family:&amp;quot;&quot;&gt;\r\n			将VR集成到设计和仿真工作流程中&amp;nbsp;&amp;nbsp;–&amp;nbsp;VR&amp;nbsp;认证的Quadro&amp;nbsp;GP100和P4000具有创建详细、逼真、沉浸式环境的能力，能够大规模实现更加宏大、更加复杂的设计体验。\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;p style=&quot;font-family:&amp;quot;&quot;&gt;\r\n			尽享照片级真实感设计的优势&amp;nbsp;–&amp;nbsp;&lt;strong&gt;基于Pascal&amp;nbsp;架构的Quadro&amp;nbsp;GPU&amp;nbsp;渲染照片级真实感图像的速度为CPU的18倍&amp;nbsp;。&lt;/strong&gt;\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;p style=&quot;font-family:&amp;quot;&quot;&gt;\r\n			创建广阔的可视化工作区&amp;nbsp;–&amp;nbsp;可在多达四个5K显示器上以高分辨率和HDR颜色显示数据。\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n	&lt;li&gt;\r\n		&lt;p style=&quot;font-family:&amp;quot;&quot;&gt;\r\n			经济高效地构建超高分辨率及多显示屏配置&amp;nbsp;–&amp;nbsp;通过将多达8个P4000&amp;nbsp;GPU和两个Quadro&amp;nbsp;Sync&amp;nbsp;II显卡相结合，可通过单一机箱实现多达32个4K显示器的配置。\r\n		&lt;/p&gt;\r\n	&lt;/li&gt;\r\n&lt;/ul&gt;\r\n&lt;h2 style=&quot;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;line-height:1.8;font-size:20px;&quot;&gt;GPU在CAE上的应用&lt;/span&gt;&lt;br /&gt;\r\n&lt;/h2&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;line-height:1.8;font-size:20px;&quot;&gt;&lt;img src=&quot;http://uppic.fd.zol-img.com.cn/t_s500x2000/g5/M00/0B/0F/ChMkJljyDXeIDB1lAAJdGKuXnKQAAbr3wPvSQkAAl0w442.jpg&quot; class=&quot;subscibe-img&quot; /&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	&lt;em&gt;&lt;span style=&quot;line-height:1.8;font-size:14px;&quot;&gt;Sandeep&amp;nbsp;Gupte&lt;/span&gt;&lt;/em&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	CAE(Computer&amp;nbsp;Aided&amp;nbsp;Engineering)指工程设计中的计算机辅助工程。英伟达专业可视化业务高级总监&amp;nbsp;Sandeep&amp;nbsp;Gupte&amp;nbsp;表示：\r\n&lt;/p&gt;\r\n&lt;blockquote style=&quot;color:#999999;font-family:&amp;quot;font-size:16px;&quot;&gt;\r\n	&lt;p style=&quot;text-indent:2em;font-style:italic !important;&quot;&gt;\r\n		对于这部分用户，大部分是使用类似&lt;a href=&quot;http://xiazai.zol.com.cn/detail/44/431635.shtml&quot; class=&quot;hui14_line&quot;&gt;Ansys&lt;/a&gt;、ABAQUS等分析&lt;a href=&quot;http://xiazai.zol.com.cn/&quot; class=&quot;hui14_line&quot;&gt;软件&lt;/a&gt;求解复杂工程和产品的结构力学性能。大规模的数据计算往往让设计分析的过程十分漫长，而如果使用CPU+GPU加速的方案将会大大的节省运算时间。\r\n	&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	GPU&amp;nbsp;加速计算是指在完成应用任务时，同时利用GPU&amp;nbsp;和&amp;nbsp;CPU。由于GPU出色的并行任务处理能力，将应用程序中计算密集部分的工作负载转移到&amp;nbsp;GPU，同时仍由&amp;nbsp;CPU&amp;nbsp;运行其余程序代码。这样，从用户的角度来看，应用程序的运行速度明显加快。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	流言终结者中有一段很有意思的视频，为我们直观的展示了相比于CPU，GPU的并行计算为什么有如此令人震撼的优势。&lt;span style=&quot;line-height:1.8;&quot;&gt;&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	&lt;iframe height=&quot;400&quot; width=&quot;600&quot; src=&quot;http://player.youku.com/embed/XNjY3MTY4NjAw&quot; frameborder=&quot;0&quot;&gt;\r\n	&lt;/iframe&gt;\r\n&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	Sandeep举了个实际的例子，在现场一个集成了一张GP100&amp;nbsp;的工作站里，工作人员使用ABAQUS对一个模型进行了470万的网格划分和计算，由于时间关系，现场并未将计算完成。这是因为类似这样大规模的计算，往往需要运行数十个小时甚至是数十天才能完成。不过Sandeep告诉雷锋网，相比仅使用CPU计算的方案，GPU加速可以将计算时间将缩短为原来的1/3。\r\n&lt;/p&gt;\r\n&lt;h2 style=&quot;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:20px;&quot;&gt;GPU在真实物理渲染（PBR）上的应用&lt;/span&gt;\r\n&lt;/h2&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	也许你觉得CAE的栗子还不够震撼，在图形渲染方面，英伟达中国区销售总监何犹卿表示，目前主流的渲染器都已经在做GPU加速平台，因为一帧4K的电影画面，如果还使用CPU渲染，需要288小时，而用GPU渲染只需要3个多小时。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:20px;font-weight:bold;line-height:1.8;&quot;&gt;GPU在深度学习上的应用&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	在深度学习领域，也是高性能GPU大展拳脚的地方，近年来深度学习的大发展除了科学家们孜孜不倦的学术探索外，和GPU运算能力的突飞猛进也密切相关。英伟达CEO黄仁勋曾表示：\r\n&lt;/p&gt;\r\n&lt;blockquote style=&quot;color:#999999;font-family:&amp;quot;font-size:16px;&quot;&gt;\r\n	&lt;p style=&quot;text-indent:2em;font-style:italic !important;&quot;&gt;\r\n		计算机科学家综合&amp;nbsp;Yann&amp;nbsp;LeCun&amp;nbsp;的卷积神经网络、Geoff&amp;nbsp;Hinton&amp;nbsp;的反向传播算法，以及&amp;nbsp;Stochastic&amp;nbsp;的梯度下降训练法，加上吴恩达&amp;nbsp;(Andrew&amp;nbsp;Ng)&amp;nbsp;对&amp;nbsp;GPU&amp;nbsp;的大规模应用，来加快深度神经网络&amp;nbsp;(DNN)&amp;nbsp;的运算速度，才促进「深度学习」这项现代人工智能技术出现大爆炸般的发展局面。\r\n	&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	对于人工智能未来将对设计工作产生的影响，Sandeep介绍了一种叫做生成式制造流程的人工智能概念设计方法。比如设计者要设计一把椅子，一般情况下需要通过&lt;a href=&quot;http://xiazai.zol.com.cn/cad_soft_index/cad_page_1.html&quot; class=&quot;hui14_line&quot;&gt;CAD&lt;/a&gt;软件设计出两三个方案，然后交由决策者选择。而有了人工智能参与后，设计者仅需提出边界条件，例如承重多少，自重多少，尺寸如何等等，人工智能就可以自动生成出上百种的设计方案供决策者挑选，这不但有助于遴选出最佳的设计方案，还能大大的缩短设计的周期。\r\n&lt;/p&gt;\r\n&lt;h2 style=&quot;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:20px;&quot;&gt;GPU在VR上的应用&lt;/span&gt;\r\n&lt;/h2&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img class=&quot;subscibe-img&quot; src=&quot;http://article.fd.zol-img.com.cn/t_s500x2000&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	在VR的应用方面，为消费级玩家所熟知的Geforce系列更多是针对个人娱乐的应用，而Quadro系列则是面向专业级的任务。如果仅仅是游戏，即便画面的真实感和准确性有些许偏差，也不会造成太严重的后果。但是如果是在工作决策中使用，那么就要求画面尽可能的逼真，这时候就需要Quadro系列显卡和&amp;nbsp;Iray&amp;nbsp;VR了。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	据Sandeep介绍，&amp;nbsp;Iray&amp;nbsp;是Nvidia的一款逼真的渲染解决方案，现已许可给&amp;nbsp;Dassault&amp;nbsp;Systèmes&amp;nbsp;和&amp;nbsp;Autodesk&amp;nbsp;等软件厂商。&amp;nbsp;利用&amp;nbsp;Iray&amp;nbsp;应用，连接至&amp;nbsp;Quadro&amp;nbsp;视觉计算设备(VCA)后，就可以享受逼真的渲染体验。&amp;nbsp;把多个&amp;nbsp;Quadro&amp;nbsp;VCA&amp;nbsp;结合到一起后，基于光场实时计算，操作模型或场景时还能够捕捉到细微的光线和反射。\r\n&lt;/p&gt;\r\n&lt;h2 style=&quot;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:20px;&quot;&gt;QUEST计划&lt;/span&gt;\r\n&lt;/h2&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	Sandeep还提到英伟达首次在国内推出了QUEST计划（Quadro&amp;nbsp;Embedded&amp;nbsp;System&amp;nbsp;Program），即基于Quadro的嵌入式系统计划，针对高性能嵌入式系统的开发者，开发企业可以申请加入此计划，英伟达将和他们合作设计适用于他们解决方案的GPU。除此之外，英伟达还会提供设计包（Design&amp;nbsp;Kit）、SDK、样本代码、训练等。该项目免费，并且没有设置申请数量的限制，但是由于致力于一同打造品质、可靠性和性能俱佳的高性能嵌入式解决方案，所以英伟达在合作伙伴的遴选上会比较谨慎。\r\n&lt;/p&gt;', '1', '2017-04-13 01:27:44', '5', '1');
INSERT INTO `d_document_for_multi` VALUES ('2', '专为医疗用途设计 谷歌母公司Alphabet推出智能手表', '/ontheroad/Uploads/gallery//58f32a53a66a5.jpg', '&lt;p align=&quot;center&quot; style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img id=&quot;content-first-img&quot; src=&quot;http://uppic.fd.zol-img.com.cn/t_s500x2000/g5/M00/0B/0E/ChMkJ1jx8F-IYNxpAAF7ZFYj43EAAbrwQPEZlQAAXt8417.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;br /&gt;\r\n&lt;span style=&quot;color:#111111;&quot;&gt;&amp;emsp;&amp;emsp;据&amp;nbsp;Slashgear&amp;nbsp;报道，谷歌母公司&amp;nbsp;Alphabet&amp;nbsp;旗下的生命科学研究机构&amp;nbsp;Verily&amp;nbsp;近日正式推出了一款&amp;nbsp;Verily&amp;nbsp;Study&amp;nbsp;智能手表，公司在一篇博文中称，这一健康跟踪可穿戴设备将用于纵向观察健康研究。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#111111;&quot;&gt;&amp;emsp;&amp;emsp;Verily&amp;nbsp;表示，这款手表具有简单、时尚的外观，并具有多种生理和环境传感器用于监测心血管、心率、惯性运动、运动障碍等领域的身体相关信号，收集到的数据将进行加密并存储于设备内，直至其被同步至&amp;nbsp;Verily&amp;nbsp;的云平台。据称&amp;nbsp;Verily&amp;nbsp;Study&amp;nbsp;智能手表可以一整周都无需充电，而设备的内置存储可以在同步之前存储多个星期的原始数据。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#111111;&quot;&gt;&amp;emsp;&amp;emsp;Verily&amp;nbsp;Study&amp;nbsp;智能手表具有“低功耗”显示屏，采用的似乎是电子纸表盘，但这一说法并未经过官方确认。根据公司的说法，为了显示日期和时间，显示器将一直处于开启状态。除了健康指标追踪和显示日期时间功能外，手表暂不具备其他通知接收功能。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;color:#111111;&quot;&gt;&amp;emsp;&amp;emsp;据悉&amp;nbsp;Verily&amp;nbsp;Study&amp;nbsp;手表不会对外出售，仅会用于研究目的。&lt;/span&gt;\r\n&lt;/p&gt;', '1', '2017-04-13 01:27:44', '2', '1');
INSERT INTO `d_document_for_multi` VALUES ('3', '你为什么愿意为了一杯喜茶排队四五个钟头？', '/ontheroad/Uploads/gallery//58f329cec87de.jpeg', '&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img id=&quot;content-first-img&quot; src=&quot;http://uppic.fd.zol-img.com.cn/t_s640x2000/g5/M00/0C/01/ChMkJ1jy0WmIGIVnAARhkM0wrBoAAbsfAGNrU0ABGGo50.jpeg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	在周六下午人流如织的广州花城汇，喜茶店面门口早已排成了长龙。在排了将近一个小时之后，我也终于拿到了自己的芝士芒芒。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	相比上海来福士广场店动辄三四个小时的排队时间，高峰期一个小时的我已经算是幸运了。起家于广东江门，喜茶如今九成的店铺都位于广东地区。不过就算店面多了，在广深地区的主要商圈，想要喝到“网红”喜茶还是得老老实实地做好排队的准备。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img src=&quot;http://uppic.fd.zol-img.com.cn/t_s640x2000/g5/M00/0C/01/ChMkJljy0WmIUX5LAALTwEMmELIAAbsfAGT9LYAAtPY894.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	（喜茶广州花城汇店）\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	一杯茶饮而已，却让这么多人为之疯狂，愿意花上数小时去排队，也是挺魔幻的。不过仔细一想，为了一样“网红美食”去排队，在现在这个时代还真不是什么新鲜事了。\r\n&lt;/p&gt;\r\n&lt;h3 style=&quot;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	排队，我是自愿的\r\n&lt;/h3&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	有人说，现在排队买喜茶的，跟当时排队买杏花楼青团、黄太吉煎饼果子、鲍师傅肉松面包、WIYE&amp;nbsp;冰淇淋的，是同一拨人。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img src=&quot;http://uppic.fd.zol-img.com.cn/t_s640x2000/g5/M00/0C/01/ChMkJ1jy0WqIR8oNAAI0cwwVwNUAAbsfAGWyI4AAjSL52.jpeg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	（图片来自：网易新闻）\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	想想有点道理，来排队的大多是十几二十几岁的年轻人，他们刷着手机、玩着自拍，排队等待的过程看上去似乎也没那么枯燥难熬了。当然，拿到成品后，第一件事就是拍照，也少不了要发朋友圈晒上一晒。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	网红美食就是这样一种神奇的东西。一个看起来非常日常的食物，一旦被挂上了“老字号”、“限量”、“名人推荐”等标签，就会身价飙升、受人追捧。而排队，更为网红美食增加了几分神秘气息，越来越成为后者的一种标配。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	想象一下，如果你走在路上，看到前面好多人在排队买一种美食，会不会停下来看两眼？如果刚好又没什么事，会不会想要排个队买来尝尝看？排队的时候用手机搜索了一下，发现竟然是特别有名的网红小吃，会不会更加坚定了你排队的决心？\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	大多数人排队都是基于这种心理，好奇和从众。尤其在吃饭这件事上，从众效应表现得更加明显。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	在没有大众点评的时代，判断一家餐厅好不好吃都是看店里的人多不多、门口排的队长不长。现在，除了看曾经来过的食客们给打了几颗星、写了什么样的点评之外，消费者们的选择还在被网红、KOL&amp;nbsp;以及各种网络舆论引导着。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	以喜茶为例，虽然已经在广东地区深耕数年，但这次上海排队事件可算是引爆了品牌在社交网络上的影响力。美妆博主@阿花花酱就曾在微博上说，近期的最大愿望就是喝到喜茶。就连王励勤都加入了喜茶大军，说球迷送的这杯在上海要排五个小时的茶，“没来得及拿银针测，回房间一口气喝完了。”\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img src=&quot;http://uppic.fd.zol-img.com.cn/t_s640x2000/g5/M00/0C/01/ChMkJljy0WuICMP8AAEU6Q6EsTgAAbsfAGZBYQAARUB560.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	如今能在朋友圈晒出一杯喜茶，已经变成了一种“赤裸裸的炫耀”。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	当越来越多的人在网上晒出“芝士莓莓”、“芝士芒芒”、“金凤茶王”等王牌产品之后，更多还没买到的人坐不住了。他们一点儿也不担心要排上几个小时，无论是在喜茶的官方账号下，还是在各大社交网络平台上，都有他们呐喊的声音：“喜茶什么时候可以开到我们这里来？”\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	换句话说，大排长龙并没有没有吓退消费者，反而让更多的人对这杯小小的奶盖茶饮产生了好奇。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img src=&quot;http://uppic.fd.zol-img.com.cn/t_s640x2000/g5/M00/0C/01/ChMkJ1jy0WyIdXQ0AAJpAQUy4c4AAbsfAGcHlAAAmkZ220.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	（图片来自：喜茶）\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	在计划经济时代，排队买东西背后是资源的稀缺，数量有限，先到先得。但是到了市场经济时代，物资丰富了，人们有了更多的选择，本不应该再被排队所困扰，却有更多的人放着不用排队的东西不买，跑去等上好几个小时。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	从前的被迫排队，到现在变成了主动选择。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	从需求的角度来看，这也算是当下时髦的“消费升级”。原来可能只追求吃饱，现在人们还要吃好。再有点高追求的，还要吃得巧、吃得妙、吃出品味和身份。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	在这个时代，吃饭早就不再是吃东西本身，吃的都是食物之上的附加价值。而对喜茶这样的网红小吃而言，排队也不再只是排队，变成了一种类似于朝圣的消费仪式。\r\n&lt;/p&gt;\r\n&lt;h3 style=&quot;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	排队买个奶茶而已，还扯上了焦虑症\r\n&lt;/h3&gt;\r\n&lt;blockquote style=&quot;color:#999999;font-family:&amp;quot;font-size:16px;&quot;&gt;\r\n	&lt;p style=&quot;text-indent:2em;font-style:italic !important;&quot;&gt;\r\n		等待能让产品增值，而增加的价值会让人们在排队时更有耐心。\r\n	&lt;/p&gt;\r\n&lt;/blockquote&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	美国芝加哥大学行为科学教授&amp;nbsp;Ayelet&amp;nbsp;Fishbach&amp;nbsp;在研究排队现象时有了这样的发现。换句话说，排队能让产品变得更值得排队。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	有不少人是的确是因为真心喜欢，觉得喜茶“用料实在、美味可口”而去排的队。在长时间的等待之后，他们拿到手的美食更显珍贵，对味道的期待值也会空前提升。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img src=&quot;http://uppic.fd.zol-img.com.cn/t_s640x2000/g5/M00/0C/01/ChMkJ1jy0WyIWH6ZAAGJkkZySlcAAbsfAGeh2kAAYmq489.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	（图片来自：喜茶）\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	但对大部分人来说，当花费大量的时间在排队上时，他们付出的成本已经远超产品本身的价值。这一部分多出来的成本，需要产品提供更多的附加价值，才能让消费者感到满足。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	对于网红美食而言，这些附加价值体现在限量销售、名人效应等方面；而另一种更具时代特征的附加价值，是它们能够提供的社交体验。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	有的人排了好几小时的队才买到一杯喜茶，但他/她的照片却获得了朋友圈的盛赞；有的人本来在微博上籍籍无名，因为晒了网红美食的照片，就引来好多人的询问互动，还因此涨了一些粉。对他们来说，这些等待的时间是值得的。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	因此就像前文所说的，追网红美食的可能真的是同一批人。他们在当代众多的“焦虑症”中，患上了一种叫做“存在焦虑症”的病。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	2013&amp;nbsp;年，牛津词典曾收录“FOMO”，即&amp;nbsp;Fear&amp;nbsp;of&amp;nbsp;Missing&amp;nbsp;Out（害怕错过）一词。它反映了在社交网络时代，人们愈加严重的“存在焦虑症”。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img src=&quot;http://uppic.fd.zol-img.com.cn/t_s640x2000/g5/M00/0C/01/ChMkJljy0WuIVQuaAAHa5CwsXfkAAbsfAGgERMAAdr8925.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	（图片来自：&lt;span class=&quot;irc_ho&quot;&gt;&lt;a class=&quot;_ZR irc_hol i3724&quot; href=&quot;https://www.theodysseyonline.com/product-the-fomo-generation&quot; target=&quot;_blank&quot;&gt;The&amp;nbsp;Odyssey&amp;nbsp;Online&lt;/a&gt;）&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	当人们在用社交网络为自己打造理想中的形象时，不可避免地陷入到社交网络的漩涡之中。“有没有错过什么网络热点？我发的段子别人觉得有意思吗？为什么没人给我点赞？”\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	渴望被认可、被赞许、被接纳，讨厌被遗忘、被忽视、被排斥。在这个时代，人们需要不停地为自己找寻存在感。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	有人把自己捧成了段子手，有人用自拍美照装点门面，但相比“突出”，很多人更害怕“落伍”和“与众不同”。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	身边的小伙伴都在玩&lt;a href=&quot;http://sj.zol.com.cn/wangzherongyao/&quot; class=&quot;hui14_line&quot;&gt;王者荣耀&lt;/a&gt;，为了跟他们有共同话题，我也玩吧；大家都在看《人民的名义》，我也得看看到底讲了什么；大家都喝了喜茶晒了照片，我也得去买一杯尝尝。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img src=&quot;http://uppic.fd.zol-img.com.cn/t_s640x2000/g5/M00/0C/01/ChMkJljy0WuIRe24AAK38MjaBWsAAbsfAGh7A8AArgI796.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	（图片来自：&lt;span class=&quot;irc_ho&quot;&gt;&lt;a class=&quot;_ZR irc_hol i3724&quot; href=&quot;https://buzzorange.com/techorange/2012/11/05/rebuzz-change-people-s-behavior-only-needs-to-change-a-word/&quot; target=&quot;_blank&quot;&gt;BuzzOrange&lt;/a&gt;）&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	说到底，这种“你有我也得有”的跟风心态是“网红们”能够火起来的源动力。社交网络上关于网红美食的文章越多，人们的“存在焦虑症”就越严重，也越想去“拔草”。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	再加上买一杯奶茶、一份点心的价格也不算贵，用可以接受的花费来换取对自己“存在焦虑症”的纾解，看上去还是划算的。至于排队，排了队才更能体现出自己的价值啊。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	喜茶好不好喝？好喝。那值不值得排&amp;nbsp;4、5&amp;nbsp;个小时去买？讲真，这就真的因人而异了。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	从实用主义者的角度来看，为了一杯奶茶去排队几个小时，可能纯粹是浪费时间；但对于有闲有钱又焦虑着的人们来说，时间并不是敏感因素，而一杯奶茶能给他们带来的，也绝非只是一杯奶茶那么简单。\r\n&lt;/p&gt;\r\n&lt;h3 style=&quot;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	你排过最长的队，可能是商家的套路\r\n&lt;/h3&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	当排队已经变成网红美食的标配，除了消费者的自发选择之外，排队也是商家最常用的一种营销策略。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	比如说星巴克就采用了横向的排队方式。在这种动线设计下，消费者的视线不会被前面的顾客遮挡，可以无障碍地获取菜单信息。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img src=&quot;http://uppic.fd.zol-img.com.cn/t_s640x2000/g5/M00/0C/01/ChMkJljy0WyISVu5AAIF-xlbcKkAAbsfAGkpBcAAgYT122.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	（图片来自：&lt;span class=&quot;irc_ho&quot;&gt;&lt;a class=&quot;_ZR irc_hol i3724&quot; href=&quot;https://www.thesun.co.uk/living/2844881/skip-starbucks-queue/&quot; target=&quot;_blank&quot;&gt;The&amp;nbsp;Sun&lt;/a&gt;）&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	同时，消费者沿着柜台横向排队，接单、制作和出品这三个主要流程都处在他们的观察之下，可以营造出一种品质有保障的感觉；充分利用横向的动线来陈列商品，也能提升消费者的购物兴趣。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	这样的设计与星巴克的价值导向也保持一致。倡导“第三空间”的星巴克，一直致力于为消费者打造一个有别于办公场所、家庭之外的休闲空间。横向排队能够让消费者看到彼此的表情，为他们提供了相互交流的机会，以缓解等待过程中的无聊。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	而麦当劳却采用了相反的纵向排队队列，它能够提醒消费者尽快做出决定、提高点单效率；也能营造出一种热闹、快节奏的氛围，符合快餐店的定位。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img src=&quot;http://uppic.fd.zol-img.com.cn/t_s640x2000/g5/M00/0C/01/ChMkJ1jy0W2IeUtMAAIDLnLCnz0AAbsfAGmqioAAgNG976.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	（图片来自：&lt;span class=&quot;irc_ho&quot;&gt;&lt;a class=&quot;_ZR irc_hol i3724&quot; href=&quot;https://www.thesun.co.uk/money/3279350/mcdonalds-has-an-app-and-it-means-youll-never-need-to-queue-again/&quot; target=&quot;_blank&quot;&gt;The&amp;nbsp;Sun&lt;/a&gt;）&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	除了排队动线设计，排队经济中被应用最广泛的就是“饥饿营销”了。通过控制消费者入场数量或者产品的供给，可以塑造出一种“产品非常抢手”的形象。再利用点人们的好奇心和从众心理，可能会产生非常好的营销效果。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	海底捞这个曾经的“网红”代表，就利用排队经济成功地“套路”了消费者。在消费者因为长时间的排队等待而放弃一家餐厅时，海底捞用免费的饮料、零食，甚至还有美甲服务留住了顾客，还因此吸引了一大批为了尝鲜而来的新客户。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	不过也有些商家在用排队营销这个大招时，太过简单粗暴，往往会招致消费者的反感。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	刚开始被爆排队时，喜茶也曾被质疑过是否是炒作和饥饿营销，网络上还流传出喜茶雇人排队的传言。一时间，大家众说纷纭。有忠实粉丝为喜茶辩护的，也有“知情人士”爆料内幕的，不过讨论归讨论，喜茶门前的队列还是那么长。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img src=&quot;http://uppic.fd.zol-img.com.cn/t_s640x2000/g5/M00/0C/01/ChMkJljy0W-IJMFVAADNhwo9xFQAAbsfAGsoxQAAM2f769.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	面对质疑，喜茶自己是感觉“委屈”的。先是表示绝对没有雇佣黄牛排队，还曾经采取标记顺序排号、限购等措施来遏制黄牛；在近日发布的公告中，喜茶还透露，此前大排长龙的上海来福士店和美罗城店的单店日出杯量已经达到了&amp;nbsp;3000&amp;nbsp;杯以上，不过仍然难以满足每日的需求量。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;img src=&quot;http://uppic.fd.zol-img.com.cn/t_s640x2000/g5/M00/0C/01/ChMkJljy0W-ICIxkAAGMtitZ9sIAAbsfAGtcLMAAYzO649.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n	（图片来自：喜茶）\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	广设新店是喜茶给出的解决方案。他们表示，除了现有的店面，接下来还将在全国更多的城市开设新店，“让大家都能愉快优雅地喝上一杯喜茶。”\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	能不能“愉快优雅地喝上”尚且不知，不过对于已经在商家的套路中摸爬滚打多年的消费者来说，“网红”来得太快就像龙卷风，但往往也会像龙卷风一样快速过气。当你捧起一杯芝士莓莓时，还记得大明湖畔的熊猫奶盖吗？\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	所以，无论是对消费者还是对商家来说，都别轻易被排队的热潮蒙蔽双眼，真诚地做出好的产品就不愁没人来买，耐心等待也早晚都能享受到。谁知道呢？也许下一家喜茶就开在你家门口。\r\n&lt;/p&gt;', '1', '2017-04-13 01:27:50', '7', '1');
INSERT INTO `d_document_for_multi` VALUES ('4', '铁路新运行图实施首日：高铁提速 11小时穿中国', '/ontheroad/Uploads/gallery//58f32969ce15d.jpg', '&lt;p&gt;\r\n	&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n		按照此前铁路总公司宣布的计划，全国铁路新的列车运行图已于今日零时起正式实施。\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;font-size:16px;color:#333333;text-align:center;font-family:&amp;quot;&quot;&gt;\r\n		&lt;span style=&quot;font-size:0px;line-height:0;&quot;&gt;&lt;/span&gt;&lt;img id=&quot;content-first-img&quot; src=&quot;http://uppic.fd.zol-img.com.cn/t_s500x2000/g5/M00/0C/02/ChMkJljy7G-ICtgRAAERDze6jgIAAbsoAAqvn0AAREn297.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n		与此前旧版运行图相比，新运行最大的特点就是增加了动车组运行线路和车次。\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n		据介绍，此次调图，铁路部门共增开动车组列车25.5对，并安排37对现有动车组列车重联或大编组运行。\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n		调整后，全国铁路开行旅客列车总数达3615对，沪昆高铁、杭深线、南广、贵广、宁蓉、京广等客流饱满线路的运力将得到扩大。\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n		目前，基于全新运行图的全国铁路开行旅客列车总数达3615对。\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n		值得一提的是，在新开动车线路的同时，现有的部分线路也&lt;a href=&quot;http://xiazai.zol.com.cn/compress_soft_index/compress_page_1.html&quot; class=&quot;hui14_line&quot;&gt;压缩&lt;/a&gt;了运行时间。\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n		比如，由昆明南始发至北京西的G404次高铁列车，运行时间压缩了2小时30分，实现单程10小时43分。从南到北横跨大半个中国还不到11个小时。\r\n	&lt;/p&gt;\r\n&lt;/p&gt;', '1', '2017-04-13 23:58:05', '7', '1');
INSERT INTO `d_document_for_multi` VALUES ('5', 'iPhone 8全新传闻曝光：之前都错了？', '/ontheroad/Uploads/gallery//58f31d84cfa51.jpg', '&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	最近关于iPhone&amp;nbsp;8的传闻非常多，据说iPhone&amp;nbsp;8会采用和Galaxy&amp;nbsp;S8类似的设计风格，屏幕尺寸高达5.7英寸左右，但屏占比会更高，甚至采用屏幕下指纹识别功能，不仅黑科技十足，颜值也几乎爆表。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	不过，今天外媒传出了另一个消息，iPhone&amp;nbsp;8可能并没有传闻中那么惊艳，只是在现款的基础上增加屏占比而已。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	外媒得到消息称，&lt;strong&gt;iPhone&amp;nbsp;8的屏幕尺寸为5.1英寸，通过提升屏占比的方式把屏幕塞入和目前4.7英寸版差不多的机身当中。&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	虽然这没有此前传闻中那么惊艳，但对果粉来说，其实已经是非常大的升级了。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	另外，今年苹果还会发布iPhone&amp;nbsp;7s和iPhone&amp;nbsp;7s&amp;nbsp;Plus，她们只是在iPhone&amp;nbsp;7家族的基础上小修小补，不会有太大惊喜。\r\n&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;font-size:16px;color:#333333;font-family:&amp;quot;&quot;&gt;\r\n	&lt;span style=&quot;font-size:0px;line-height:0;&quot;&gt;&lt;/span&gt;&lt;img id=&quot;content-first-img&quot; src=&quot;http://uppic.fd.zol-img.com.cn/t_s500x2000/g5/M00/0B/0F/ChMkJljyKTGIQ9DOAAFDqqNvlOMAAbsCgB1V5sAAUPC241.jpg&quot; class=&quot;subscibe-img&quot; /&gt;\r\n&lt;/p&gt;', '1', '2017-04-14 00:00:04', '7', '1');
