-- ----------------------------
-- Table structure for d_room_type
-- ----------------------------
DROP TABLE IF EXISTS `d_room_type`;
CREATE TABLE `d_room_type` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `thumbnail` text COMMENT '缩略图',
  `name` varchar(255) DEFAULT NULL COMMENT '房型名称',
  `price` decimal(10,0) DEFAULT NULL COMMENT '价格',
  `brief` varchar(255) DEFAULT NULL COMMENT '房型简介',
  `detail` text COMMENT '房型详述',
  `state` tinyint(2) DEFAULT '1',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

