INSERT INTO `d_room_type` VALUES ('1', '/ontheroad/Uploads/roomType/58ea661c88bf9.JPG', '天空', '199', '天空之城', '&lt;h1&gt;\r\n	&lt;em&gt;&lt;u&gt;天空之城&lt;/u&gt;&lt;/em&gt;\r\n&lt;/h1&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;/ontheroad/Public/Admin/kindeditor/attached/image/20170409/20170409164751_22983.jpg&quot; alt=&quot;&quot; width=&quot;620&quot; height=&quot;465&quot; title=&quot;&quot; align=&quot;&quot; /&gt; \r\n&lt;/p&gt;', '0');
